/*
 * This is the primary high-level API for the project. In this folder there should be:
 * A class called InsightFacade, this should be in a file called InsightFacade.ts.
 * You should not change this interface at all or the test suite will not work.
 */

export enum InsightDatasetKind {
	Sections = "sections",
	Rooms = "rooms",
}

export interface InsightDataset {
	id: string;
	kind: InsightDatasetKind;
	numRows: number;
}

export type InsightResult = Record<string, string | number>;

export class InsightError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, InsightError);
	}
}

export class NotFoundError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, NotFoundError);
	}
}

export class ResultTooLargeError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, ResultTooLargeError);
	}
}

export interface IInsightFacade {
	/**
	 * Add a dataset to insightUBC.
	 *
	 * @param id  The id of the dataset being added. Follows the format /^[^_]+$/
	 * @param content  The base64 content of the dataset. This content should be in the form of a serialized zip file.
	 * @param kind  The kind of the dataset
	 *
	 * @return Promise <string[]>
	 *
	 * The promise should fulfill on a successful add, reject for any failures.
	 * The promise should fulfill with a string array,
	 * containing the ids of all currently added datasets upon a successful add.
	 * The promise should reject with an InsightError describing the error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 * If id is the same as the id of an already added dataset, the dataset should be rejected and not saved.
	 *
	 * After receiving the dataset, it should be processed into a data structure of
	 * your design. The processed data structure should be persisted to disk; your
	 * system should be able to load this persisted value into memory for answering
	 * queries.
	 *
	 * Ultimately, a dataset must be added or loaded from disk before queries can
	 * be successfully answered.
	 */
	addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]>;

	/**
	 * Remove a dataset from insightUBC.
	 *
	 * @param id  The id of the dataset to remove. Follows the format /^[^_]+$/
	 *
	 * @return Promise <string>
	 *
	 * The promise should fulfill upon a successful removal, reject on any error.
	 * Attempting to remove a dataset that hasn't been added yet counts as an error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 *
	 * The promise should fulfill the id of the dataset that was removed.
	 * The promise should reject with a NotFoundError (if a valid id was not yet added)
	 * or an InsightError (invalid id or any other source of failure) describing the error.
	 *
	 * This will delete both disk and memory caches for the dataset for the id meaning
	 * that subsequent queries for that id should fail unless a new addDataset happens first.
	 */
	removeDataset(id: string): Promise<string>;

	/**
	 * Perform a query on insightUBC.
	 *
	 * @param query  The query to be performed.
	 *
	 * If a query is incorrectly formatted, references a dataset not added (in memory or on disk),
	 * or references multiple datasets, it should be rejected.
	 *
	 * @return Promise <InsightResult[]>
	 *
	 * The promise should fulfill with an array of results.
	 * The promise should reject with a ResultTooLargeError (if the query returns too many results)
	 * or an InsightError (for any other source of failure) describing the error.
	 */
	performQuery(query: unknown): Promise<InsightResult[]>;

	/**
	 * List all currently added datasets, their types, and number of rows.
	 *
	 * @return Promise <InsightDataset[]>
	 * The promise should fulfill an array of currently added InsightDatasets, and will only fulfill.
	 */
	listDatasets(): Promise<InsightDataset[]>;
}

import {
	IInsightFacade,
	InsightDataset,
	InsightDatasetKind,
	InsightError,
	InsightResult,
	NotFoundError,
	ResultTooLargeError,
} from "./IInsightFacade";
import fs from "fs-extra";
import DatasetValidatorHelper from "./DatasetValidatorHelper";
import QueryEngine from "./QueryEngine";
import SectionDiskReader from "./sections/SectionDiskReader";
import SectionDiskWriter from "./sections/SectionDiskWriter";
import Section from "./sections/Section";
import RoomDiskReader from "./rooms/RoomDiskReader";
import RoomDiskWriter from "./rooms/RoomDiskWriter";
import Room from "./rooms/Room";
import AddSectionDataset from "./sections/addSectionDataset";

/**
 * This is the main programmatic entry point for the project.
 * Method documentation is in IInsightFacade
 *
 */
export default class InsightFacade implements IInsightFacade {
	// tracks all sections added from a dataset using their associated id as the key
	public sectionsDatabase: Map<string, Section[]>;
	public roomsDatabase: Map<string, Room[]>;

	// service classes
	private datasetValidatorHelper: DatasetValidatorHelper;
	private qe: QueryEngine;
	private secDiskReader: SectionDiskReader;
	private secDiskWriter: SectionDiskWriter;
	private roomDiskReader: RoomDiskReader;
	private roomDiskWriter: RoomDiskWriter;
	private sectionHelper: AddSectionDataset;

	constructor() {
		//Log.info("InsightFacadeImpl::init()");
		this.sectionsDatabase = new Map<string, []>();
		this.roomsDatabase = new Map<string, Room[]>();
		this.datasetValidatorHelper = new DatasetValidatorHelper();
		this.qe = new QueryEngine(this.sectionsDatabase, this.roomsDatabase);
		this.secDiskReader = new SectionDiskReader();
		this.secDiskWriter = new SectionDiskWriter();
		this.roomDiskReader = new RoomDiskReader();
		this.roomDiskWriter = new RoomDiskWriter();

		this.sectionHelper = new AddSectionDataset();
		// initialize dictionary for the fields
	}

	public async addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]> {
		try {
			await this.datasetValidatorHelper.validateIdStructure(id);
			await this.datasetValidatorHelper.validateSectionAddition(id, this.sectionsDatabase, this.roomsDatabase);

			if (kind === InsightDatasetKind.Sections) {
				return this.sectionHelper.addCourses(id, this.sectionsDatabase, content, kind);
			} else {
				await this.roomDiskWriter.logRoomsDatasetOnDisk(content, id, this.roomsDatabase);
				return fs.readdir("./data");
			}
		} catch (err) {
			if (err instanceof InsightError) {
				throw err;
			}
			throw new InsightError(`An unexpected error occurred: ${err}`);
		}
	}

	public async removeDataset(id: string): Promise<string> {
		try {
			await this.datasetValidatorHelper.validateIdStructure(id);
			const datasetPath = `./data/${id}`;
			if (!(await fs.pathExists(datasetPath))) {
				throw new NotFoundError(`Dataset with id ${id} not found.`);
			}

			if (this.sectionsDatabase.get(id)) {
				this.sectionsDatabase.delete(id);
			} else {
				this.roomsDatabase.delete(id);
			}

			await fs.remove(`./data/${id}`);

			// return id name of set currently removed
			return id;
		} catch (err) {
			if (err instanceof InsightError) {
				throw new InsightError("");
			}

			if (err instanceof NotFoundError) {
				throw new NotFoundError("Not found");
			}
			// Handle unexpected errors
			throw new InsightError("An unexpected error occurred ${err.message}");
		}
	}

	public async performQuery(query: unknown): Promise<InsightResult[]> {
		let result: InsightResult[] = [];
		try {
			const currIDs = await fs.readdir("./data");
			//console.log(currIDs)
			const idRecords = await this.datasetValidatorHelper.separateRoomAndCourseIDs(currIDs);
			//console.log(idRecords.rooms, idRecords.courses)

			if (this.sectionsDatabase.size + this.roomsDatabase.size < currIDs.length) {
				const cIDs = idRecords.sections;
				const rIDs = idRecords.rooms;
				//console.log(rIDs)
				this.sectionsDatabase = await this.secDiskReader.mapMissingSections(cIDs, this.sectionsDatabase);
				//console.log((this.sectionsDatabase.keys))
				this.roomsDatabase = await this.roomDiskReader.mapMissingRooms(rIDs, this.roomsDatabase);
			}
			//console.log(currIDs);
			// on query, passes updated sectionsDatabase and roomsDatabase
			result = await this.qe.query(query, this.sectionsDatabase, this.roomsDatabase);
		} catch (err) {
			if (err instanceof InsightError || err instanceof ResultTooLargeError) {
				throw err;
			} else {
				throw new InsightError("Unexpected error.");
			}
		}
		return result;
	}

	public async listDatasets(): Promise<InsightDataset[]> {
		// reads the list of dataset ids already on disk

		const currIDs = await fs.readdir("./data");

		// reads their content info on disk and parses into InsightDataset[](works for rooms as well)
		return this.secDiskReader.logInsightKindFromDisk(currIDs);
	}
}

import { InsightError } from "./IInsightFacade";
import fs from "fs-extra";
import Section from "./sections/Section";
import Room from "./rooms/Room";

export default class DatasetValidatorHelper {
	// checks if a dataset id is valid to be added

	// checks if a dataset id is valid to be removed
	public async validateIdStructure(id: string): Promise<void> {
		// Validate ID follows proper format

		if (id.includes("_") || id.trim().length === 0) {
			throw new InsightError("Invalid ID structure");
		}

		// Validate content based on its kind
		const base64Regex = /^[^_]+$/;
		if (!base64Regex.test(id)) {
			throw new InsightError("Invalid id");
		}
	}

	public async validateSectionAddition(
		id: string,
		sectionsDatabase: Map<string, Section[]>,
		roomsDatabase: Map<string, Room[]>
	): Promise<void> {
		if (sectionsDatabase.has(id) || roomsDatabase.has(id) || (await fs.pathExists(`./data/${id}`))) {
			throw new InsightError(`Dataset with id ${id} already exists.`);
		}
	}

	public async separateRoomAndCourseIDs(allIds: string[]): Promise<{ sections: string[]; rooms: string[] }> {
		const setIds = new Set(allIds);
		const sectionIDs: string[] = [];
		const roomIDs: string[] = [];

		const results = await Promise.all(
			Array.from(setIds).map(async (id) => {
				const coursePath = `./data/${id}/courses`;

				return fs
					.pathExists(coursePath)
					.then((pathExists) => ({
						id,
						isCourse: pathExists,
					}))
					.catch((err) => {
						throw err;
					});
			})
		);

		results.forEach(({ id, isCourse }) => {
			if (isCourse) {
				sectionIDs.push(id);
			} else {
				roomIDs.push(id);
			}
		});

		// console.log('Final Course IDs:', courseIDs);
		// console.log('Final Room IDs:', roomIDs);

		return { sections: sectionIDs, rooms: roomIDs };
	}
}

import { InsightError, InsightResult, ResultTooLargeError } from "./IInsightFacade";
import { QueryOrderHandler } from "./QueryOrderHandler";
import QueryUtils from "./QueryUtils";
import QueryEngineFilter from "./QueryEngineFilter";
import Section from "./sections/Section";
import Room from "./rooms/Room";
//import DatasetValidatorHelper from "./DatasetValidatorHelper";
import QueryAggregation from "./QueryAggregation";

export default class QueryEngine {
	private sectionsDatabase: Map<string, Section[]>;
	private roomsDatabase: Map<string, Room[]>;
	private utils: QueryUtils;
	private QueryOrderHandler: QueryOrderHandler;
	private QueryEngineFilter: QueryEngineFilter;
	private QueryAggregation: QueryAggregation;
	private sDSList: string[];
	private rDSList: string[];
	private newCols: string[] = [];
	private queryingIDString = "";
	private sectionOrRoom = "";
	private isGrouped = false;
	private dir = "UP";
	private noFilter = false;
	//private datasetValidator: DatasetValidatorHelper;

	constructor(sectionsDatabase: Map<string, Section[]>, roomsDatabase: Map<string, Room[]>) {
		this.sectionsDatabase = sectionsDatabase;
		this.roomsDatabase = roomsDatabase;
		this.utils = new QueryUtils();
		this.QueryOrderHandler = new QueryOrderHandler();
		this.QueryEngineFilter = new QueryEngineFilter(this.sectionsDatabase, this.roomsDatabase);
		this.QueryAggregation = new QueryAggregation(this.sectionsDatabase, this.roomsDatabase);
		this.sectionOrRoom = "";
		//this.datasetValidator = new DatasetValidatorHelper();
		this.sDSList = [];
		this.rDSList = [];
		//console.log(this.rDSList);
	}

	private async querySetup(
		sectionsDatabase: Map<string, Section[]>,
		roomsDatabase: Map<string, Room[]>
	): Promise<boolean> {
		/*
		const idRecords = await this.datasetValidator.separateRoomAndCourseIDs(currIDs);
		this.rDSList = idRecords.rooms;
		this.sDSList = idRecords.sections;
		*/
		this.sectionsDatabase = sectionsDatabase;
		this.roomsDatabase = roomsDatabase;
		this.sDSList = Array.from(sectionsDatabase.keys());
		this.rDSList = Array.from(roomsDatabase.keys());
		this.QueryEngineFilter = new QueryEngineFilter(this.sectionsDatabase, this.roomsDatabase);
		this.QueryAggregation = new QueryAggregation(this.sectionsDatabase, this.roomsDatabase);
		this.sectionOrRoom = "";
		this.queryingIDString = ""; // restart on every query;
		this.isGrouped = false;
		this.dir = "UP";
		this.newCols = [];
		this.noFilter = false;
		return true;
	}

	public async query(query: unknown, sD: Map<string, Section[]>, rD: Map<string, Room[]>): Promise<InsightResult[]> {
		//console.log("QUERY method");
		//console.log(this.roomsDatabase.size);
		await this.querySetup(sD, rD);
		//console.log(this.rDSList);
		let filteredSOR: Object[] = [];
		let transformedResults: Object[] = [];
		let result: InsightResult[] = [];
		const queryObj = Object(query);
		//console.log(queryObj)
		try {
			const queryKeys = Object.keys(queryObj);
			//console.log(queryKeys)
			const invalidKeys = queryKeys.filter((key) => !this.utils.validQueryKeys.includes(key));
			if (invalidKeys.length > 0) {
				throw new InsightError("Excess keys in query");
			}
			// If WHERE key exists, filter all the sections, else throw InsightError
			if ("WHERE" in queryObj) {
				filteredSOR = await this.handleWHERE(queryObj.WHERE);
				//console.log(filteredSOR)
			} else {
				throw new InsightError("Query missing WHERE");
			}
			//console.log(filteredSOR);

			// If TRANSFORMATIONS key exists, complete transformation on filtered sections
			if ("TRANSFORMATIONS" in queryObj) {
				transformedResults = await this.handleTRANSFORMATIONS(queryObj.TRANSFORMATIONS, filteredSOR);
				//this.isGrouped = true;
			} else {
				transformedResults = filteredSOR;
			}
			//console.log(transformedResults);

			// If OPTIONS key exists, collect InsightResults, else throw InsightError
			if ("OPTIONS" in queryObj) {
				result = await this.handleOPTIONS(queryObj.OPTIONS, transformedResults);
			} else {
				throw new InsightError("Query missing OPTIONS");
			}
			//console.log("End result");
			//console.log(result);
		} catch (err) {
			if (err instanceof InsightError || err instanceof ResultTooLargeError) {
				throw err;
			}
			throw new InsightError("Unexpected error.");
		}
		return result;
	}

	// handling section or room in filter
	private async handleWHERE(where: object): Promise<Object[]> {
		let filteredSOR: Object[] = [];
		this.noFilter = false;
		//console.log("WHERE WORKING");
		try {
			if (Object.keys(where).length === 0) {
				this.noFilter = true;
				return filteredSOR;
			} else if (Object.keys(where).length > 1) {
				throw new InsightError("WHERE should only have 1 key");
			} else {
				const filter = Object.keys(where)[0];
				const values = Object.values(where)[0];

				//TODO;
				//console.log(filter, values)
				filteredSOR = await this.QueryEngineFilter.handleFilter(filter, values);
				//console.log(filteredSOR)
				this.queryingIDString = this.QueryEngineFilter.queryingIDString;

				this.sectionOrRoom = this.QueryEngineFilter.sectionOrRoom;
			}
		} catch (err) {
			if (err instanceof InsightError || err instanceof ResultTooLargeError) {
				throw err;
			}
			throw new InsightError("Unexpected error in WHERE.");
		}
		return filteredSOR;
	}

	//TODO;
	private async handleTRANSFORMATIONS(transformations: object, filteredSOR: Object[]): Promise<Object[]> {
		let transformedResults: Object[] = [];
		let groupedResults: Record<string, any>;
		//const groupKeys;
		this.isGrouped = true;
		try {
			const transformationKeys = Object.keys(transformations);
			const invalidKeys = transformationKeys.filter((key) => !this.utils.validTransformationKeys.includes(key));
			if (invalidKeys.length > 0) {
				throw new InsightError("Invalid keys in TRANSFORMATIONS");
			}
			if ("GROUP" in transformations) {
				groupedResults = await this.QueryAggregation.handleGroupBy(
					transformations.GROUP,
					filteredSOR,
					this.noFilter,
					this.queryingIDString,
					this.sectionOrRoom
				);
			} else {
				throw new InsightError("TRANSFORMATIONS missing GROUP key");
			}

			if ("APPLY" in transformations) {
				transformedResults = await this.QueryAggregation.handleApply(transformations.APPLY, groupedResults);
				this.updateVar();
				//console.log("Promise resolved with:", transformedResults);
			} else {
				throw new InsightError("TRANSFORMATIONS missing APPLY key");
			}
		} catch (err) {
			if (err instanceof InsightError || err instanceof ResultTooLargeError) {
				throw err;
			}
			throw new InsightError("Unexpected error in TRANSFORMATIONS.");
		}
		return transformedResults;
	}

	private updateVar(): boolean {
		this.queryingIDString = this.QueryAggregation.queryingIDString;
		this.sectionOrRoom = this.QueryAggregation.sectionOrRoom;
		this.newCols = this.QueryAggregation.groupKeys.concat(this.QueryAggregation.applyKeys);
		//console.log(this.newCols);
		return true;
	}

	private async handleOPTIONS(options: object, transformedResults: Object[]): Promise<InsightResult[]> {
		//console.log("OPTIONS WORKING");
		let results: InsightResult[] = [];
		let columns: string[] = [];
		let orderKeys: string[] = [];
		try {
			const optionsKeys = Object.keys(options);
			const invalidKeys = optionsKeys.filter((key) => !this.utils.validOptions.includes(key));
			if (invalidKeys.length > 0) {
				throw new InsightError("Invalid keys in OPTIONS");
			}

			// Done
			if ("COLUMNS" in options) {
				columns = this.handleCOLUMNS(options.COLUMNS);
			} else {
				throw new InsightError("Query missing COLUMNS");
			}

			//console.log(columns);
			// Done
			if ("ORDER" in options) {
				orderKeys = await this.QueryOrderHandler.handleORDER(options.ORDER, columns);
				this.dir = this.QueryOrderHandler.getDir();
			}

			results = await this.completeQuery(transformedResults, columns, orderKeys);
		} catch (err) {
			if (err instanceof InsightError || err instanceof ResultTooLargeError) {
				throw err;
			}
			throw new InsightError("Unexpected error in OPTIONS.");
		}
		return results;
	}

	private async completeQuery(
		transformedResults: Object[],
		columns: string[],
		orderKeys: string[]
	): Promise<InsightResult[]> {
		let results: InsightResult[] = [];
		let dataset: Object[] | undefined = [];
		//console.log("COMPLETE QUERY WORKING");
		// if no filters have been applied
		if (this.noFilter && !this.isGrouped) {
			if (this.sectionOrRoom === "section") {
				dataset = this.sectionsDatabase.get(this.queryingIDString);
			} else if (this.sectionOrRoom === "room") {
				dataset = this.roomsDatabase.get(this.queryingIDString);
			} else {
				throw new InsightError("section or room undefined");
			}
			if (dataset === undefined) {
				// should not be possible given current implementation of other methods for query
				throw new InsightError("Can't find querying dataset");
			} else {
				transformedResults = dataset;
			}
		}

		//console.log(transformedResults.length);
		this.utils.checkSize(transformedResults);

		//TODO
		results = await this.utils.selectCOLUMNS(transformedResults, columns, this.isGrouped);
		results = await this.utils.sortByOrder(results, orderKeys, this.dir);
		return results;
	}

	// returns the columns as an array of strings (WORKING)
	private handleCOLUMNS(value: unknown): string[] {
		//console.log("COLUMNS WORKING");
		const columns = this.utils.coerceToArray(value); // checks if is an array, if so, coerce to array type
		const results: string[] = [];
		for (const key of columns) {
			const keyStr = String(key);
			const field = keyStr.split("_")[1];
			const idstring = keyStr.split("_")[0];

			if (this.checkValidTransformColumn(keyStr)) {
				results.push(keyStr);
			} else {
				this.sectionOrRoom = this.utils.checkIDString(
					this.sDSList,
					this.rDSList,
					this.sectionOrRoom,
					this.queryingIDString,
					idstring
				);
				this.queryingIDString = idstring;

				// checks if the field is a valid field
				if (this.checkValidColumn(field)) {
					results.push(keyStr);
				} else {
					throw new InsightError(`Invalid key ${keyStr} in COLUMNS`);
				}
			}
		}
		return results;
	}

	private checkValidTransformColumn(keyStr: string): boolean {
		if (this.isGrouped) {
			if (this.newCols.includes(keyStr)) {
				return true;
			} else {
				throw new InsightError("Keys in COLUMNS must be in GROUP or APPLY when TRANSFORMATIONS is present");
			}
		} else {
			return false;
		}
	}

	// if the field is valid, return true, if sectionOrRoom is somehow empty throw error
	private checkValidColumn(field: string): boolean {
		//const field = keyStr.split("_")[1];
		if (this.sectionOrRoom === "section") {
			if (this.utils.mFieldsSection.includes(field) || this.utils.sFieldsSection.includes(field)) {
				return true;
			}
		} else if (this.sectionOrRoom === "room") {
			if (this.utils.mFieldsRoom.includes(field) || this.utils.sFieldsRoom.includes(field)) {
				return true;
			}
		} else {
			throw new InsightError("section or room undefined");
		}
		return false;
	}
}

import { InsightError } from "./IInsightFacade";
import QueryUtils from "./QueryUtils";

export class QueryOrderHandler {
	// returns the order key as a string (WORKING)
	private validSort: string[] = ["dir", "keys"];
	private validDir: string[] = ["UP", "DOWN"];
	private utils = new QueryUtils();
	private dir = "";

	public getDir(): string {
		return this.dir;
	}

	public async handleORDER(value: unknown, columns: string[]): Promise<string[]> {
		let orderKeys: string[] = [];
		this.dir = "UP";

		// if only one order key
		if (typeof value === "string") {
			//console.log(value);
			//console.log("order as a string");
			const valueStr = String(value);
			orderKeys.push(this.checkKeyInColumns(valueStr, columns));
		} else if (typeof value === "object") {
			//console.log("order as an object");
			//console.log(value);
			const sortObj = Object(value);
			const sortKeys = Object.keys(sortObj);
			const invalidKeys = sortKeys.filter((key) => !this.validSort.includes(key));

			//console.log(sortKeys);
			if (invalidKeys.length > 0) {
				throw new InsightError("Invalid keys in ORDER");
			}

			if ("dir" in sortObj) {
				//console.log(typeof sortObj.dir);
				//console.log("reached dir in sorKeys");
				//console.log(sortObj.dir);
				// changed from sortObj.dir in this.validDir
				if (this.validDir.includes(sortObj.dir)) {
					//console.log("WORKING");
					this.dir = sortObj.dir;
				} else {
					//console.log("Test");
					//console.log(sortObj.dir);
					throw new InsightError("Invalid 'dir' value");
				}
			} else {
				throw new InsightError("ORDER missing 'dir' key");
			}

			if ("keys" in sortObj) {
				orderKeys = await this.handleOrderKeys(sortObj.keys, columns);
			} else {
				throw new InsightError("ORDER missing 'keys' key");
			}
		} else {
			throw new InsightError("Invalid ORDER type");
		}
		return orderKeys;
	}

	private async handleOrderKeys(value: unknown, columns: string[]): Promise<string[]> {
		const orderKeys: string[] = [];
		if (this.utils.isStringArray(value)) {
			const keys = value as string[];
			if (keys.length === 0) {
				throw new InsightError("ORDER keys must be a non-empty array");
			}
			for (const key of keys) {
				orderKeys.push(this.checkKeyInColumns(key, columns));
			}
		} else {
			throw new InsightError("ORDER keys must be an array of strings");
		}
		return orderKeys;
	}

	// checks if the key is in columns, if not, throw error
	private checkKeyInColumns(key: string, columns: string[]): string {
		if (columns.includes(key)) {
			return key;
		} else {
			throw new InsightError("ORDER key must be in COLUMNS");
		}
	}
}

import { InsightError, InsightResult, ResultTooLargeError } from "./IInsightFacade";

export default class QueryUtils {
	public logicComparator: string[] = ["AND", "OR"];
	public mComparator: string[] = ["LT", "GT", "EQ"];
	public sFieldsSection: string[] = ["uuid", "id", "title", "instructor", "dept"];
	public mFieldsSection: string[] = ["year", "avg", "pass", "fail", "audit"];
	public sFieldsRoom: string[] = ["fullname", "shortname", "number", "name", "address", "type", "furniture", "href"];
	public mFieldsRoom: string[] = ["lat", "lon", "seats"];
	public validOptions: string[] = ["COLUMNS", "ORDER"];
	public validQueryKeys: string[] = ["WHERE", "OPTIONS", "TRANSFORMATIONS"];
	public validTransformationKeys: string[] = ["GROUP", "APPLY"];
	public validApplyTokens: string[] = ["MAX", "MIN", "AVG", "SUM", "COUNT"];
	//public validSort: string[] = ["dir", "keys"];

	public coerceToArray(value: unknown): unknown[] {
		if (Array.isArray(value)) {
			return value;
		} else {
			throw new InsightError("Not an array.");
		}
	}

	public checkSize(transformedResults: Object[]): boolean {
		const maxQuerySize = 5000;
		if (transformedResults.length > maxQuerySize) {
			throw new ResultTooLargeError(
				"The result is too big. Only queries with a maximum of 5000 results are supported."
			);
		} else {
			return true;
		}
	}

	// DONE?
	public async sortByOrder(results: InsightResult[], orderKeys: string[], dir: string): Promise<InsightResult[]> {
		if (orderKeys.length === 0) {
			return results;
		} else {
			// Perform the sorting asynchronously
			return new Promise((resolve) => {
				setTimeout(() => {
					results.sort((recordA, recordB) => this.sortFunction(recordA, recordB, orderKeys, dir));
					// Resolve the promise with the sorted results
					resolve(results);
				}, 0);
			});
		}
	}

	// helper function for sortByOrder
	private sortFunction(recordA: InsightResult, recordB: InsightResult, orderKeys: string[], dir: string): number {
		for (const key of orderKeys) {
			const valueA = recordA[key];
			const valueB = recordB[key];
			let comparison = 0;

			if (
				(typeof valueA === "string" && typeof valueB === "string") ||
				(typeof valueA === "number" && typeof valueB === "number")
			) {
				comparison = valueA === valueB ? 0 : valueA < valueB ? -1 : 1;
			} else {
				const strA = String(valueA);
				const strB = String(valueB);
				comparison = strA === strB ? 0 : strA < strB ? -1 : 1;
			}

			// if theres a difference --> apply the sorting direction and move to the next key to break tie
			if (comparison !== 0) {
				return dir === "UP" ? comparison : -comparison;
			}
		}
		return 0;
	}

	// select columns of the transformedResults (transformedResults may be a transformed objects, sections, or rooms)
	public async selectCOLUMNS(
		transformedResults: Object[],
		columns: string[],
		isGrouped: boolean
	): Promise<InsightResult[]> {
		const results = transformedResults.map((result) => {
			const currRecord: InsightResult = {};
			const resultObj = result as Record<string, any>;

			columns.forEach((column) => {
				const field = column.split("_")[1];
				// using the implementation of individuals fields of rooms and sections
				if (isGrouped) {
					currRecord[column] = resultObj[column];
				} else {
					currRecord[column] = resultObj[field];
				}
			});

			return currRecord;
		});
		return results;
	}

	// REQUIRES: mfield valid, input is a number, dataset is valid, filter is valid
	// helper function for handleMCompare
	public async filterMCompare(dataset: Object[], filter: string, mfield: string, input: number): Promise<Object[]> {
		let results: Object[];
		//console.log("FILTER MCOMPARISON WORKING");
		if (filter === "LT") {
			results = dataset.filter((SOR) => (SOR as Record<string, any>)[mfield] < input);
		} else if (filter === "GT") {
			results = dataset.filter((SOR) => (SOR as Record<string, any>)[mfield] > input);
		} else if (filter === "EQ") {
			results = dataset.filter((SOR) => (SOR as Record<string, any>)[mfield] === input);
		} else {
			throw new InsightError("Invalid MComparator");
		}
		return results;
	}

	public async mergeAndList(andList: Object[][]): Promise<Object[]> {
		// make a map
		const SORCountMap = new Map<Object, number>();

		// find the shortest list
		const shortestList = andList.reduce((shortest, currArray) => {
			return currArray.length < shortest.length ? currArray : shortest;
		});

		// turn into a set for faster lookup
		const shortestSet = new Set(shortestList);

		// add all section in shortest list to map and increment count by 1
		shortestSet.forEach((SOR) => {
			SORCountMap.set(SOR, (SORCountMap.get(SOR) || 0) + 1);
		});

		// iterate through each array of section
		for (const currArray of andList) {
			// skip comparison with itself
			if (currArray === shortestList) {
				continue;
			}

			const currSet = new Set(currArray);

			// only keep sections or rooms in the shortest list that are also in the current array
			shortestList.forEach((SOR) => {
				if (!currSet.has(SOR)) {
					SORCountMap.delete(SOR); // Remove sections not found in currArray
				}
			});
		}

		// filter the shortest list to include only sections present in all arrays
		return shortestList.filter((SOR) => SORCountMap.has(SOR));
	}

	public isObject(obj: unknown): void {
		if (Array.isArray(obj)) {
			throw new InsightError("Invalid object present");
		}
	}

	public testRegex(input: string): RegExp {
		const validInputRegex = /^[*]?[^*]*[*]?$/;
		if (!validInputRegex.test(input)) {
			throw new InsightError(" Asterisks (*) can only be the first or last characters of input strings");
		}
		// fix this return, figure out what sfield is, how to match it, and how to access
		const processedInput = input.replace(/\*/g, ".*");
		return new RegExp(`^${processedInput}$`); // Use case-insensitive matching
	}

	// checks if the idstring is valid, if it is return the type of dataset it refers to
	public checkIDString(
		sDSList: string[],
		rDSList: string[],
		sectionOrRoom: string,
		queryingIDString: string,
		idStr: string
	): string {
		// check if a dataset has already been referenced if not return whether its a section or a room DS
		//console.log(queryingIDString);
		if (queryingIDString === "") {
			return this.checkSectionOrRoom(sDSList, rDSList, idStr);
		} else if (queryingIDString !== idStr) {
			throw new InsightError("Cannot reference multiple datasets.");
		} else {
			return sectionOrRoom;
		}
	}

	// checks if the idstr is in sectionsDS or roomsDS, otherwise throw error that the ds is not added
	public checkSectionOrRoom(sDSList: string[], rDSList: string[], idStr: string): string {
		if (sDSList.includes(idStr)) {
			return "section";
		} else if (rDSList.includes(idStr)) {
			return "room";
		} else {
			throw new InsightError(`Dataset with id: ${idStr} not added.`);
		}
	}

	// checks if the array is a string array
	public isStringArray(value: unknown): boolean {
		return Array.isArray(value) && value.every((item) => typeof item === "string");
	}
}

