	private applyRulesRecursive(
		groupedResults: Record<string, any> | Object[],
		applyRules: Record<string, [string, string]>,
		groupKeyIterator: number,
		keyList: string[]
	): Object[] {
		const results: Object[] = [];
		if (groupKeyIterator === this.groupKeys.length) {
			const SORArr: Object[] = groupedResults as Object[];
			const group = this.makeNewObject(SORArr, keyList, applyRules);
			results.push(group);
		} else {
			//const resultsPromises: Promise<Object[]>[] = [];
			for (const currkey of Object.keys(groupedResults)) {
				const newKeyList = keyList.concat(currkey);
				results.push(
					...this.applyRulesRecursive(
						(groupedResults as Record<string, any>)[currkey],
						applyRules,
						groupKeyIterator + 1,
						newKeyList
					)
				);
			}
			/*
			try {
				//console.log("reached promise");

				const promises = await Promise.allSettled(resultsPromises);
				results = this.checkAllFulfilled(promises);
				//console.log(loadDatasetPromises.length);
			} catch (err) {
				throw new Error(`Failed apply rules recursively, error: ${err}`);
			}
			*/
		}
		return results;
	}

	/*
	private checkAllFulfilled(promises: PromiseSettledResult<Object[]>[]): Object[] {
		const allFulfilled = promises.every((promise) => promise.status === "fulfilled");
		if (allFulfilled) {
			const objectArr = promises.map((promise) => (promise as PromiseFulfilledResult<any>).value);
			console.log(objectArr);
			return objectArr;
		} else {
			throw new InsightError("One of the recursives failed");
		}
	}*/

	private makeNewObject(SORArr: Object[], keyList: string[], applyRules: Record<string, [string, string]>): Object {
		const group: Record<string, any> = {};
		for (let i = 0; i < this.groupKeys.length; i++) {
			group[this.groupKeys[i]] = this.checkNumberOrStringField(keyList[i], this.groupKeys[i]);
		}
		for (const applyKey of Object.keys(applyRules)) {
			group[applyKey] = this.applyAFunction(applyRules[applyKey][0], applyRules[applyKey][1], SORArr);
		}
		//console.log(group);
		return group;
	}

	private checkNumberOrStringField(groupVal: string, groupKey: string): string | number {
		//const field = groupKey.split("_")[1];
		// Use this to debug
		if (this.checkTargetKey(groupKey) === "number") {
			return Number(groupVal);
		} else {
			return groupVal;
		}
	}

	private applyAFunction(token: string, targetKey: string, SORArr: Object[]): Object {
		const field = targetKey.split("_")[1];
		const targetFieldList: any[] = [];
		const decimals = 2;

		for (const SOR of SORArr) {
			//console.log(SOR);
			targetFieldList.push((SOR as Record<string, any>)[field]);
		}
		//console.log("apply a function, token:");
		//console.log(token);
		//console.log(targetKey);
		if (this.checkTargetKey(targetKey) === "number") {
			if (token === "MAX") {
				return Math.max(...targetFieldList);
			} else if (token === "MIN") {
				return Math.min(...targetFieldList);
			} else if (token === "AVG") {
				const total: Decimal = targetFieldList.reduce((acc, num) => Decimal.add(acc, num), new Decimal(0));
				const numRows: number = targetFieldList.length;
				const avg = total.toNumber() / numRows;
				return Number(avg.toFixed(decimals));
			} else if (token === "SUM") {
				const sum = Number(targetFieldList.reduce((acc, num) => Decimal.add(acc, num), new Decimal(0)));
				return Number(sum.toFixed(decimals));
			} else if (token === "COUNT") {
				return Array.from(new Set(targetFieldList)).length;
			} else {
				throw new InsightError("Invalid apply token");
			}
		} else {
			if (token === "COUNT") {
				return Array.from(new Set(targetFieldList)).length;
			} else {
				throw new InsightError("Invalid apply token");
			}
		}
	}

	/*
	private calculateAVG(targetFieldList: number[]): number {
		const total = new Decimal(0);
		const numRows = targetFieldList.length;
		for (let i = 0; i < targetFieldList.length; i++) {
			Decimal.add(total);
		}
	}

	private calculateSUM(targetFieldList: number[]): number {}

	*/
	private checkTargetKey(targetKey: string): string {
		const targetField = targetKey.split("_")[1];
		if (this.sectionOrRoom === "section") {
			if (this.utils.mFieldsSection.includes(targetField)) {
				return "number";
			} else {
				return "string";
			}
		} else if (this.sectionOrRoom === "room") {
			if (this.utils.mFieldsRoom.includes(targetField)) {
				return "number";
			} else {
				return "string";
			}
		} else {
			throw new InsightError("section or room not defined");
		}
	}

	private async checkApplyRules(apply: unknown): Promise<Record<string, [string, string]>> {
		const applyRules: Record<string, [string, string]> = {};
		const applyArr = this.utils.coerceToArray(apply);
		const validApplyKeyRegex = /^[^_]+$/;

		for (const rule of applyArr) {
			if (typeof rule !== "object") {
				throw new InsightError("Apply rule invalid type");
			} else {
				const ruleObj = Object(rule);
				if (Object.keys(ruleObj).length !== 1) {
					throw new InsightError("Apply rule should have only one key");
				}
				const applyKey = Object.keys(ruleObj)[0];
				const applyTokenAndKey = Object.values(ruleObj)[0];
				if (!validApplyKeyRegex.test(applyKey)) {
					throw new InsightError("Apply key cannot contain underscores");
				} else if (applyKey in applyRules) {
					throw new InsightError("Cannot have duplicate apply keys");
				}
				const TokenAndKeyPair = this.checkTokenAndKey(applyTokenAndKey);
				applyRules[applyKey] = TokenAndKeyPair;
			}
		}
		return applyRules;
	}

	private checkTokenAndKey(applyTokenAndKey: unknown): [string, string] {
		if (typeof applyTokenAndKey !== "object") {
			throw new InsightError("Apply key invalid type");
		} else {
			const applyTokenAndKeyObj = Object(applyTokenAndKey);
			if (Object.keys(applyTokenAndKeyObj).length !== 1) {
				throw new InsightError("applytoken should be the only key");
			}

			const token = this.checkValidToken(Object.keys(applyTokenAndKeyObj)[0]);
			const key = Object.values(applyTokenAndKeyObj)[0];
			//console.log(token);
			//console.log(key);

			if (typeof key !== "string") {
				throw new InsightError("APPLY invalid target key type");
			}
			const field = key.split("_")[1];
			if (this.checkValidKey(field)) {
				return [token, String(key)];
			} else {
				throw new InsightError("APPLY invalid target key");
			}
		}
	}

	private checkValidToken(token: unknown): string {
		if (typeof token !== "string") {
			throw new InsightError("APPLY invalid applytoken type");
		} else {
			const tokenStr = String(token);
			if (this.utils.validApplyTokens.includes(tokenStr)) {
				return tokenStr;
			} else {
				throw new InsightError("APPLY invalid applytoken");
			}
		}
	}
}

import { InsightDataset, InsightDatasetKind, InsightError } from "../IInsightFacade";
import SectionsParser, { DatasetRecord } from "./SectionsParser";
import fs from "fs-extra";
import Section from "./Section";

// Any information about Sections that need to read from the disk

export default class SectionDiskReader extends SectionsParser {
	constructor() {
		super();
	}

	// REQUIRES: currIDs - array of all the dataset ids currently added in InsightFacade instance
	//			 sectionsDatabase - map of all added datasets and their associated sections
	// EFFECTS: finds all sections datasets in disks not in current datasets and returns a map of the datasets with their id (dataset name)
	// and associated sections
	// datasetsIds = all currently added datasets (refer to currIDs),
	public async mapMissingSections(
		currIDs: string[],
		sectionsDatabase: Map<string, Section[]>
	): Promise<Map<string, Section[]>> {
		const allPromises: Promise<DatasetRecord>[] = [];
		// the id of all datasets not currently added

		currIDs.forEach((setId) => {
			// all ids for missing datasets are returned as a Record
			// with all the sections associated with the id
			const promise = this.turnDatasetToSection(setId);
			allPromises.push(promise);
		});

		const records = await Promise.all(allPromises);
		// add all records collected to Map
		records.forEach((record) => {
			sectionsDatabase.set(record.id, record.sections);
		});

		return sectionsDatabase;
	}

	// Writes InsightDataset info about a dataset from disk
	public async logInsightKindFromDisk(ids: string[]): Promise<InsightDataset[]> {
		const allPromises = ids.map(async (id) => {
			const file = await fs.promises.readFile(`./data/${id}/kind`, "utf8");
			const obj = JSON.parse(file);

			const numRows = obj.table[0].numRows as number;
			const kind = obj.table[0].kind as InsightDatasetKind;

			const newInsightDataset: InsightDataset = {
				id: id,
				kind: kind,
				numRows: numRows,
			};

			return newInsightDataset;
		});

		const result = await Promise.all(allPromises);
		return result;
	}

	// REQUIRES: id - name of dataset to be retrieved from disk (id IS NOT IN datasets ALREADY!!!!)
	//           datasets - sets you'll be mapping new DatasetRecord to
	// EFFECTS: Retrieves the sections associated with the dataset id on disk and turned into Sections objects and maps
	//          them to sectionsDatabase with their associated id.
	// OUTPUT: VOID
	public async logNewDatasetFromDiskToMap(id: string, sectionsDatabase: Map<string, Section[]>): Promise<void> {
		const newDataset = await this.turnDatasetToSection(id);
		const numRows = newDataset.sections.length;

		if (numRows === 0) {
			throw new InsightError("No valid Section");
		}
		// update member variables
		sectionsDatabase.set(newDataset.id, newDataset.sections);
	}
}

import fs from "fs-extra";
import Section, { Mfield, Sfield } from "./Section";

export interface DatasetRecord {
	id: string;
	sections: Section[];
}

export default class SectionsParser {
	private valid_fields: string[] = [
		"Year",
		"Course",
		"Title",
		"Professor",
		"Subject",
		"id",
		"Avg",
		"Pass",
		"Fail",
		"Audit",
	];

	// Keep order of mFields and sFields according to chart found in Section Specification sheet
	// for consistency
	public mFields: string[] = ["Year", "Avg", "Pass", "Fail", "Audit"];

	public sFields: string[] = ["id", "Course", "Title", "Professor", "Subject"];

	private static OVERALL_SECTION_YEAR = 1900;

	// REQUIRES: jsonData - parsed JSON Object of the result key in a given course file
	//
	// EFFECTS: Iterate through each section in a given course file and for each section obtain all the sfields and mfields
	// 			and checks that each section contains all the fields in the variable valid_fields, else filter out.
	//
	// OUTPUT: returns all the valid sections as a JSONObject.
	public filterValidSections(jsonData: any): any {
		const hasAllValidFields = (section: any, validFields: string[]): boolean => {
			const fieldKeys = Object.keys(section);
			return validFields.every((field) => fieldKeys.includes(field));
		};

		const validSectionsInCourse = jsonData.result.filter((section: any) =>
			hasAllValidFields(section, this.valid_fields)
		);
		return validSectionsInCourse;
	}

	// REQUIRES: id - name of dataset
	// EFFECTS: A helper function that can be used by performQuery to turn a dataset written in the disk into a
	// 			DatasetRecord  -> a key value pair of the all the valid sections associated with the given dataset id.
	// OUTPUT:  DatasetRecord, mapping the list of Sections to its associated dataset id
	public async turnDatasetToSection(id: string): Promise<DatasetRecord> {
		// tracks number of sections in a given dataset and is initialized to 0

		// where each promise is appended to for each course object
		const allPromises: any[] = [];
		const sections: Section[] = [];

		// list of all courses under the dataset file
		const path = await fs.readdir(`./data/${id}/courses/`);
		for (const course of path) {
			const promise = fs
				.readJson(`./data/${id}/courses/${course}`)
				.then(async (file) => {
					if (file.result.length === 0) {
						return null;
					}
					const validSectionsInCourse = this.filterValidSections(file);
					file.result = validSectionsInCourse;
					// turn all valid sections to Sections objects
					validSectionsInCourse.forEach((section: any) => {
						if (section.Section === "overall") {
							const newSection = this.createSection(section, id);
							newSection.setMfield(newSection.getMFieldIndex("year"), SectionsParser.OVERALL_SECTION_YEAR);
							newSection.year = SectionsParser.OVERALL_SECTION_YEAR;
							sections.push(newSection);
						} else {
							sections.push(this.createSection(section, id));
						}
					});

					return { id, file };
				})
				.catch((err) => {
					throw err;
				});

			allPromises.push(promise);
		}

		await Promise.all(allPromises);
		const datasetRecord: DatasetRecord = { id: id, sections: sections };
		return datasetRecord;
	}

	// REQUIRES: jsonData - parsed JSON Object of a valid section from the result key in a given course file
	// EFFECTS: Retrieves the fields of the section and populate the values of the sfields and mfields into a Section object
	// OUTPUT: newly populated and created Section object
	private createSection(jsonData: any, setId: string): Section {
		const result = jsonData;

		const [uuid, id, title, instructor, dept]: string[] = this.sFields.map((sfield) => {
			const value = result[sfield] as string;
			return value;
		});

		const sectionSfields: Sfield = {
			uuid: String(uuid),
			id: String(id),
			title: String(title),
			instructor: String(instructor),
			dept: String(dept),
		};

		//console.log(typeof sectionSfields.uuid)

		const [year, avg, pass, fail, audit] = this.mFields.map((mfield) => result[mfield]);

		const sectionMfields: Mfield = {
			year: Number(year),
			avg: Number(avg),
			pass: Number(pass),
			fail: Number(fail),
			audit: Number(audit),
		};

		const newSection: Section = new Section(setId, sectionMfields, sectionSfields);
		//console.log(typeof newSection.getSfields().id)
		return newSection;
	}
}

